import { Box, Button, Dialog, TextField, Typography,styled } from "@mui/material";
import React,{useState} from "react";

const Component = styled(Box)`
height:70vh;
width:90vh;
`;

const Image = styled(Box)`
background: #2874f0 url(https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png) center 85% no-repeat;
height: 80%;
width:28%;
padding:45px 35px;
& > p, & > h5 {
    color:#fff;
    font-weight:600;
}
`;

const Wrapper =styled(Box)`
display:flex;
flex-direction:column;
padding:25px 35px;
flex:1;
& > div, & > button, & >p{
    margin-top:20px;
}
`;

const LoginButton = styled(Button)`
text-transform:none;
background:#FB641B;
color:#fff;
height:48px;
border-radius:2px;

`;
const RequestOTP = styled(Button)`
text-transform:none;
background:#fff;
color:#2874f0;
height:48px;
border-radius:2px;
box-shadow:0 2px 4px 0 rgb(0 0 0/ 20%);
`;

const Text = styled(Typography)`
font-size: 12px;
color:#878787;
`;

const CreateAccount = styled(Typography)`
font-size:14px;
text-align:center;
color:#2874f0;
font-weight:700;
cursor:pointer;
;`


const accountInitialValue = {
    login:{
        view:'login',
        heading:'Login',
        subheading:'Get access to your Orders, Wishlist and Recommendations'
    },
    signup:{
        view:'signup',
        heading:"Looks like you're new here!",
        subheading:"Sign up with your mobile number to get started"
    }
}
const LoginDialog = ({ open, setOpen }) => {
    const [account,toggleAccount] = useState(accountInitialValue.login);

  const handleClose = () => {
    setOpen(false);
    toggleAccount(accountInitialValue.login)
  };

  const toggleSignup = ()=>{
    toggleAccount(accountInitialValue.signup)
  }

  return (
    <Dialog open={open} onClose={handleClose} PaperProps={{sx:{maxWidth:'unset'}}}>
      <Component>
        <Box style={{display:'flex', height:'100%'}}>
        <Image>
            <Typography variant="h5">{account.heading}</Typography>
            <Typography style={{marginTop:20}}>{account.subheading}</Typography>
        </Image>
        {
           account.view ==='login' ?
           <Wrapper>
           <TextField
             variant="standard"
             label="Enter Email/Mobile Number"
           ></TextField>
           <TextField variant="standard" label="Enter Password"></TextField>
           <Text>
             By continuing, you agree to Flipkart's Terms of Use and Privacy
             Policy.
           </Text>
           <LoginButton>Login</LoginButton>
           <Typography style={{textAlign:'center'}}>OR</Typography>
           <RequestOTP>Request OTP</RequestOTP>
           <CreateAccount onClick={()=>toggleSignup()}>New to Flipkart? Create an account</CreateAccount>
         </Wrapper>
         :
        <Wrapper>
          <TextField
            variant="standard"
            label="Enter First Name"
          ></TextField>
          <TextField variant="standard" label="Enter Last Name"></TextField>
          <TextField variant="standard" label="Enter Username"></TextField>
          <TextField variant="standard" label="Enter Email"></TextField>
          <TextField variant="standard" label="Enter Password"></TextField>
          <TextField variant="standard" label="Enter Phone"></TextField>
          <LoginButton>Continue</LoginButton>
          <CreateAccount>New to Flipkart? Create an account</CreateAccount>
        </Wrapper>
        
}
        </Box>
      </Component>
    </Dialog>
  );
};

export default LoginDialog;
